// import api from './api';

const authService = {
  login: async (username, password) => {
      try {
          // Perform login operation and return response
          // Example:
          const response = await fetch('http://localhost:8000/api/login', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json'
              },
              body: JSON.stringify({
                  username: username,
                  password: password
              })
          });
          if (response.ok) {
              return await response.json(); // Return response data
          } else {
              throw new Error('Login failed'); // Throw error if login fails
          }
      } catch (error) {
          throw new Error('Login failed'); // Throw error if an exception occurs
      }
  }
};


export default authService;
