import React, { useState } from 'react';
import '../../css/addcategorey.css';
const AddCategory = () => {
  const [category, setCategory] = useState({
    videoUrl: '',
    videoName: '',
    videoCategory: ''
  });
  const [categories, setCategories] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCategory(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (category.videoUrl.trim() !== '' && category.videoName.trim() !== '' && category.videoCategory.trim() !== '') {
      setCategories(prevState => [...prevState, category]);
      setCategory({
        videoUrl: '',
        videoName: '',
        videoCategory: ''
      });
    }
  };

  return (
    <div className="add-category">
      <h2>Add Category</h2>
      <form onSubmit={handleSubmit}>

        <div className="form-group">
          <label htmlFor="videoName">Video Name:</label>
          <input type="text" id="videoName" name="videoName" value={category.videoName} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label htmlFor="videoCategory">Video Category:</label>
          <input type="text" id="videoCategory" name="videoCategory" value={category.videoCategory} onChange={handleChange} />
        </div>
        <button type="submit">Add Category</button>
      </form>
      <div className="category-list">
        <h3>Categories:</h3>
        <ul>
          {categories.map((cat, index) => (
            <li key={index}>
              <strong>Name:</strong> {cat.videoName} <br />
              <strong>Category:</strong> {cat.videoCategory} <br />
              <strong>URL:</strong> {cat.videoUrl}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AddCategory;
