import React, { useState } from 'react';
import '../../css/addvideo.css';
import {addvideos} from '../../services/addvideos'

const AddVideo = () => {
  const [videoData, setVideoData] = useState({
    videoUrl: '',
    videoName: '',
    videoCategory: ''
  });
  const addVideo = async () => {
    try {
      const res = await addvideos(videoData);
      alert('Video added successfully ')
      console.log(res);
    } catch (error) {
      console.error(error);
    }
  };
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setVideoData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addVideo();
    console.log("Video Data:", videoData);
  };

  return (
    <div className="add-video">
      <h2>Add Video</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="videoUrl">Video URL:</label>
          <input type="text" id="videoUrl" name="videoUrl" value={videoData.videoUrl} onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label htmlFor="videoName">Video Name:</label>
          <input type="text" id="videoName" name="videoName" value={videoData.videoName} onChange={handleInputChange} />
        </div>
        <div className="form-group">
          <label htmlFor="videoCategory">Video Category:</label>
          <select id="videoCategory" name="videoCategory" value={videoData.videoCategory} onChange={handleInputChange}>
            <option value="">Select category...</option>
            <option value="REST001">Restaurant</option>
            <option value="HOTL002">Hotel</option>
            <option value="BTQ003">Boutique Shop</option>
            <option value="COFF004">Coffee Shop</option>
            {/* Add more options for categories as needed */}
          </select>
        </div>
        <button type="submit">Add Video</button>
      </form>
    </div>
  );
};

export default AddVideo;
