// Login.js

import React, { useState } from 'react';
import { useNavigate} from 'react-router-dom';
import authService from '../../services/authService';
import '../../css/login.css'; // Import the CSS file

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false); // State variable to manage loading state
   const navigate=useNavigate();


    const handlleLogin = () => {
      setLoading(true); // Start loading when login is initiated

      authService(username,password)
      .then((response) => {
        console.log("response", response)
        if (response.success) {
          navigate('/Dashboard');
        } else {
          setLoading(false);
          console.error('Login failed:', response.Message);
        }
      })
      .catch((error) => {
        setLoading(false);
        console.error('Login error:', error);
        Alert.alert('Enter Correct Username & Password');
      })
      .finally(() => {
        setLoading(false);
      });
    };
  return (
    <div className="login-container">
      <form className="login-form">
        <h2 className="login-heading">Login</h2>
        <input
          className="input-field"
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          className="input-field"
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button className="login-button" onClick={handlleLogin}>
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
