// import api from './api';

const authService = async (username, password) => {
      try {
          const response = await fetch('your_login_endpoint', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json'
              },
              body: JSON.stringify({
                  username: username,
                  password: password
              })
          });

          if (response.ok) {
              // Return the response if login is successful
              return await response.json();
          } else {
              // Handle error response
              throw new Error('Login failed');
          }
      } catch (error) {
          console.error('Login error:', error);
          throw error;
      }
  };


export default authService;
